mapper.select:
select cartId
from cart
where memberId = #{userId}

select cartId, cartitemid, productId, quantity, price
from cart_list
where cartId = #{memberId}

select totalQuantity, totalPrice
from cart
where cartId = #{cartId}

mapper.delete:
delete
from cart_list
where cartId= #{cartId}
 and productId = #{productId}

mapper.update:
update cart_list
set quantity = #{quantity}, price = #{price}
where cartId = #{cartId}
 and productId = #{productId}

mapper.select:
select cartId
from cart
where cartId = #{cartid}

select productId
from product
where productId = #{productId}

select price
from product
where productId = #{productId}

select count(*)
from cart_list
where cartId = #{cartId}
 and productId = #{productId}

select productId
from cart_list
where cartId = #{cartId}
  and productId = #{productId}

mapper.update:
update cart
set
    totalPrice = (
        select sum(price)
        from cart_list
        where cartId = #{cartId}
    ),
    totalquantity = (
        select sum(quantity)
        from cart_list
        where cartId = #{cartId}
    )
where cartId = #{cartId};
